settings.markerImageFunction:
var aeration = data['aeration'];
var cropLevel = data['cropLevel'];
var index = Math.floor(cropLevel / 20);
if (aeration === 'true') {
    index += 5;
}
var res = {
    url: images[index],
    size: 60  
};
return res;

settings.tooltipFunction:
var deviceType = dsData[dsIndex]['Type'];
if (typeof deviceType !== undefined) {
    if (deviceType == "energy meter") {
        return '<b>${entityName}</b><br/><b>Energy:</b> ${energy:2} kWt<br/>';
    } else if (deviceType == "thermometer") {
        return '<b>${entityName}</b><br/><b>Temperature:</b> ${temperature:2} °C<br/>';
    }
}

